package stringcoding;

public class substring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		    String str = "I am working in capgemini";
	       
	        //prints substring from 5th index
	     
	        System.out.println(str.substring(5));
	        // prints substring from 5-12, exclusive 6th index
	   
	        System.out.println(str.substring(5, 12));
	        // prints the substring from 16-25, exclusive 22th index
	     
	        System.out.println(str.substring(16, 25));
	}

}
